mapper.select:
SELECT COUNT(*)
FROM admin_account
WHERE emp_id = #{empId}

SELECT a.*, d.dept_name
FROM admin_account a JOIN department d ON a.dept_id = d.dept_id
WHERE admin_id = #{adminId}

SELECT a.*, d.dept_name
FROM admin_account a JOIN department d ON a.dept_id = d.dept_id
WHERE emp_id = #{empId}
