mapper.update:
update drug_check_detail
 set is_checked = 'CHECK'
 where detail_id = #{detailId}

mapper.select:
select dc.drug_code from drug_check dc
join drug_check_detail dcd on dc.check_id = dcd.check_id
where dcd.detail_id = #{detailId};

mapper.update:
update drug
set quantity = quantity -  #{quantity}
where drug_code = #{drugCode}

mapper.select:
SELECT dl.*, d.drug_name FROM drug_log dl join drug d on d.drug_code = dl.drug_code
AND dl.drug_code = #{drugCode}
ORDER BY dl.created_at
DESC ASC
LIMIT #{size} OFFSET #{offset}
