mapper.update:
update drug_check_detail
 set is_checked = 'CHECK'
 where detail_id = #{detailId}

mapper.select:
select dc.drug_code from drug_check dc
join drug_check_detail dcd on dc.check_id = dcd.check_id
where dcd.detail_id = #{detailId};

mapper.update:
update drug_purchase
set quantity = quantity -  #{quantity}
where purchase_id = #{purchaseId}

mapper.select:
SELECT dl.log_id AS logId, dl.drug_code AS drugCode, dl.type AS type, dl.quantity AS quantity, dl.before_stock AS beforeStock, dl.after_stock AS afterStock, dl.memo AS memo, dl.created_at AS createdAt, dl.created_by AS createdBy, d.drug_name AS drugName FROM drug_log dl join drug d on d.drug_code = dl.drug_code
AND dl.drug_code = #{drugCode}
ORDER BY dl.created_at
DESC ASC
LIMIT #{size} OFFSET #{offset}
