mapper.select:
SELECT *
FROM finance_transaction



        AND type = #{type}




        AND category = #{category}




        AND status = #{status}




        AND created_at >= #{startDate}


        AND created_at <= #{endDate}


ORDER BY created_at

    ASC
    DESC

SELECT *
FROM finance_transaction
WHERE ref_type = #{refType}
AND ref_id = #{refId}
ORDER BY created_at DESC
LIMIT 1
