mapper.select:
SELECT * FROM patient
(name LIKE CONCAT('%', #{keyword}, '%') OR phone LIKE CONCAT('%', #{keyword}, '%'))
ORDER BY patient_id DESC LIMIT #{size} OFFSET #{offset}

SELECT COUNT(*)
FROM medical_record
WHERE patient_id = #{patientId}

mapper.update:
UPDATE patient
SET admission_status = #{admissionStatus}
WHERE patient_id = #{patientId}

UPDATE patient
SET room_id = #{roomId}
WHERE patient_id = #{patientId}
