mapper.select:
SELECT r.reservation_id AS reservationId, ts.schedule_id AS scheduleId, ts.test_code AS testCode, ts.test_name AS testName, ts.test_date AS testDate, ts.test_time AS testTime, r.status AS status, p.name AS patientName FROM test_reservation r JOIN test_schedule ts ON r.schedule_id = ts.schedule_id JOIN test_fee f ON ts.test_code = f.test_code JOIN patient p ON r.patient_id = p.patient_id WHERE UPPER(TRIM(f.test_group)) = UPPER(TRIM(#{group}))
AND (p.name LIKE CONCAT('%', #{keyword}, '%') OR ts.test_name LIKE CONCAT('%', #{keyword}, '%'))
AND ts.test_date >= #{startDate}
AND ts.test_date <= #{endDate}
ORDER BY ts.test_date DESC, ts.test_time ASC
