mapper.update:
UPDATE test_schedule
SET test_date =#{testDate}, test_time=#{testTime}
WHERE schedule_id = #{scheduleId}

UPDATE test_schedule
SET reserved = reserved + 1
WHERE schedule_id = #{scheduleId}

UPDATE test_schedule
SET reserved = CASE WHEN reserved > 0 THEN reserved - 1 ELSE 0 END
WHERE schedule_id = #{scheduleId}
